import React, { useState, useEffect } from 'react';
import { Title, Div, Img, Counter, Button } from './style';

import logoAcens from '../src/logo.png';

function App() {

  const [counter, setCounter] = useState(0);
  const [davo, setDavo] = useState(true);

  function counterAdd () {
    setCounter( counter + 1 );
  }

  function lombraTorta () {
    davo === false ? setDavo(true) : setDavo(false);
  }

  function handleAll () {
    counterAdd();
    lombraTorta();
  }

  return (
    <Div>
      <Title>Contador do Davo</Title>
      <Img davo={davo}src={logoAcens} alt="acens"/>
      <Counter davo={davo}>{counter}</Counter>
      <Button onClick={handleAll}>{counter === 0 ? 'Vamo Contar?' : 'Adicionar 1'} </Button>
    </Div>
  );
}

export default App;
